mapper.select:
SELECT cg.categoryId_n AS categoryId, cg.categoryName_m AS categoryName, cg.path_x AS path, cg.classifyId_n AS classifyId, cg.seq_c AS sequence, cg.active_c AS active, DATE_FORMAT(cg.crtOn_dt, '%Y-%m-%d %H:%i') AS createDateStr, cg.crtBy_m AS createBy, cf.classifyName_m AS classifyName, cf.status_x AS status FROM cf_category cg LEFT JOIN cf_classify cf ON cg.classifyId_n = cf.classifyId_n
WHERE cg.classifyId_n = #{classifyId, jdbcType=INTEGER}
ORDER BY cf.seq_c ASC, cg.seq_c ASC
