mapper.select:
SELECT r.recordId_n AS recordId, r.type_x AS type, r.late_c AS late, r.count_n AS count, r.weight_n AS weight, r.price_n AS price, r.amount_n AS amount, r.desc_x AS description, r.record_dt AS recordDate, DATE_FORMAT(r.record_dt, '%Y-%m-%d') AS recordDateStr, r.active_c AS active, r.crtBy_m AS createBy, r.crtOn_dt AS createDate, c.value_x AS typeDisplay FROM cf_record r LEFT JOIN cf_code c ON r.type_x = c.name_x WHERE r.type_x = #{type} AND r.active_c = 'A'
r.record_dt BETWEEN #{startDate} AND #{endDate}
ORDER BY r.record_dt DESC, r.crtOn_dt DESC

mapper.update:
UPDATE cf_record SET active_c = 'I'


        AND type_x = #{type, jdbcType=VARCHAR}


        AND record_dt = #{recordDate, jdbcType=TIMESTAMP}
